QrCodeEncodingOptions options = new QrCodeEncodingOptions{  DisableECI = true, CharacterSet = "UTF-8", Width = 250, Height = 250};
var writer = new ZXing.BarcodeWriter();
writer.Format = BarcodeFormat.QR_CODE;
writer.Options = options;
byte[] qrCodeAsPngByteArr = null;
var qr = new ZXing.BarcodeWriter();
qr.Options = options;
qr.Format = ZXing.BarcodeFormat.QR_CODE;
var result = new Bitmap(qr.Write(inputValue));
ImageConverter imgCon = new ImageConverter();
var image = result;
var maxWidth = 250;
var maxHeight = 250;
var ratioX = (double)maxWidth / image.Width;
var ratioY = (double)maxHeight / image.Height;
var ratio = Math.Min(ratioX, ratioY);
var newWidth = (int)(image.Width * ratio);
var newHeight = (int)(image.Height * ratio);
var newImage = new Bitmap(newWidth, newHeight);
using (var graphics = Graphics.FromImage(newImage))
graphics.DrawImage(image, 0, 0, newWidth, newHeight);
qrCodeAsPngByteArr = (byte[])(new ImageConverter()).ConvertTo(newImage , typeof(byte[]));
UdfLoadImageData imageData = new UdfLoadImageData();
imageData.ImageTag = Base64Encoding.Encode(qrCodeAsPngByteArr);
imageData.ImageType = UdfLoadImageType.Db;
return imageData;

