try
{
    using (NpgsqlConnection pgConn = new NpgsqlConnection(connectionStr))
    {
        pgConn.Open();
        NpgsqlCommand cmd = new NpgsqlCommand();
        cmd.Connection = pgConn;

        cmd.CommandText = sql;
        cmd.CommandType = CommandType.Text;
        result = cmd.ExecuteScalar().ToString();
    }
}
catch (Exception e)
{
    sessionInfo.WriteLog(String.Format("Ginesys Custom Function Log : {0}", e.Message));
    sessionInfo.WriteLog(String.Format("Ginesys Custom Function Log : {0}", e.StackTrace));                
}
return result;

