if (vAppAPIURL != null)
{
    var appAPIURL = vAppAPIURL.Value;                
    string[] urlSplits = appAPIURL.Split(new string[] { "|~GINNI~|" }, StringSplitOptions.None);

    appAPIURL = urlSplits[0];
    var accessToken = urlSplits[1];                
    System.Uri geturi = null;
    if (!string.IsNullOrEmpty(accessToken))
    {                    
        geturi = new Uri(appAPIURL + "/ItemMaster/GetItemThumbImageForReport?itemImageName=" + imageName + "&thumbImage=true&thumbWidth=" + width + "&thumbHeight=" + height);
    }
    else
    {                 
        geturi = new Uri(appAPIURL + "/ItemMaster/GetItemThumbImageForReport?itemImageName=" + imageName + "&thumbWidth=" + width + "&thumbHeight=" + height);
    }
	sessionInfo.WriteLog(String.Format("Item Imege url-1 : : {0}", geturi));
	WebReports.Api.Common.Logger.Instance().Fatal(String.Format("Item Imege url-2 : : {0}", geturi));	            
	ServicePointManager.ServerCertificateValidationCallback +=
		(certificateSender, cert, chain, sslPolicyErrors) => { return true; };

	System.Net.ServicePointManager.SecurityProtocol = SecurityProtocolType.Tls12 | SecurityProtocolType.Tls11 | SecurityProtocolType.Tls;
    System.Net.Http.HttpClient client = new System.Net.Http.HttpClient();
    client.DefaultRequestHeaders.Accept.Clear();
    System.Net.ServicePointManager.Expect100Continue = false;
    client.DefaultRequestHeaders.Accept.Add(new System.Net.Http.Headers.MediaTypeWithQualityHeaderValue("application/json"));
    client.DefaultRequestHeaders.Authorization = new System.Net.Http.Headers.AuthenticationHeaderValue("Bearer", accessToken);
    System.Net.Http.HttpResponseMessage responseGet = client.GetAsync(geturi).Result;

    if (responseGet.IsSuccessStatusCode)
    {
        // by calling .Result you are performing a synchronous call
        var responseContent = responseGet.Content;

        // by calling .Result you are synchronously reading the result
        string responseString = responseContent.ReadAsStringAsync().Result;                  
        var imageRef = new { itemImageData = "", imageExtension = "" };
        var resultDefination = new { Result = imageRef, Success = false, Error = "" };

        var data = Newtonsoft.Json.JsonConvert.DeserializeAnonymousType(responseString, resultDefination);

        if (data.Success)
        {                       
            UdfLoadImageData imageData = new UdfLoadImageData();
            imageData.ImageTag = data.Result.itemImageData;
            imageData.ImageType = UdfLoadImageType.Db;
            return imageData;
        }
        else
        {                       
            UdfLoadImageData imageData = new UdfLoadImageData();
            imageData.ImageTag = errorImg;
            imageData.ImageType = UdfLoadImageType.Db;
            return imageData;
        }

    }
    else
    {                    
        UdfLoadImageData imageData = new UdfLoadImageData();
        imageData.ImageTag = errorImg;
        imageData.ImageType = UdfLoadImageType.Db;
        return imageData;
    }
}
else
{                
    UdfLoadImageData imageData = new UdfLoadImageData();
    imageData.ImageTag = errorImg;
    imageData.ImageType = UdfLoadImageType.Db;
    return imageData;
}
